// Extract file tree from GitHub API response
const treeData = $input.first().json.tree;
const config = $('Configuration').first().json;
const owner = config.owner;
const repo = config.repo;
const branch = config.branch;
const maxFiles = config.maxFilesToProcess || 100;

// Define testable file extensions by language/framework
const testableExtensions = {
  javascript: ['.js', '.jsx', '.mjs', '.cjs'],
  typescript: ['.ts', '.tsx'],
  python: ['.py'],
  java: ['.java', '.kt', '.kts'],
  csharp: ['.cs'],
  go: ['.go'],
  ruby: ['.rb'],
  php: ['.php'],
  rust: ['.rs'],
  swift: ['.swift'],
  cpp: ['.c', '.cpp', '.cc', '.cxx', '.h', '.hpp'],
  scala: ['.scala'],
  vue: ['.vue'],
  svelte: ['.svelte']
};

const allTestableExtensions = Object.values(testableExtensions).flat();

// Enhanced exclusion: Paths and directories that contain test files
const excludePaths = [
  'test', 'tests', '__tests__', 'spec', 'specs',
  '__test__', '__spec__', 'testing',
  'e2e', 'integration', 'unit',
  'fixtures', 'mocks', '__mocks__',
  'node_modules', 'vendor', 'dist', 'build', 'out',
  '.git', '.github', '.vscode', '.idea',
  'coverage', 'docs', 'documentation',
  'examples', 'demo', 'samples'
];

// Enhanced exclusion: File name patterns commonly used for tests
const excludeFilenames = [
  // Config files
  'jest.config', 'webpack.config', 'babel.config',
  'tsconfig', 'eslint', 'prettier', 'vitest.config',
  'karma.config', 'mocha.config', 'jasmine.config',
  // Test file patterns - middle of filename
  '.test.', '.spec.', '_test.', '_spec.',
  '.tests.', '.specs.', '_tests.', '_specs.',
  // Test file patterns - start of filename
  'test.', 'spec.', 'test_', 'spec_',
  // Test file patterns - end of filename (before extension)
  'test', 'spec', 'tests', 'specs',
  // Mock and fixture patterns
  'mock', 'mocks', '.mock.', '_mock.',
  'fixture', 'fixtures', '.fixture.', '_fixture.',
  'stub', 'stubs', '.stub.', '_stub.',
  // Setup and helper test files
  'setup', 'testsetup', 'test-setup', 'test_setup',
  'helper', 'testhelper', 'test-helper', 'test_helper',
  // Framework-specific patterns
  'testcase', 'test-case', 'test_case',
  'unittest', 'unit-test', 'unit_test'
];

const sourceFiles = treeData
  .filter(item => {
    if (item.type !== 'blob') return false;

    const path = item.path.toLowerCase();
    const filename = path.split('/').pop();
    const filenameWithoutExt = filename.substring(0, filename.lastIndexOf('.')) || filename;

    // Must have a testable extension
    const hasTestableExt = allTestableExtensions.some(ext => 
      item.path.endsWith(ext)
    );
    if (!hasTestableExt) return false;

    // Exclude files in test-related directories
    const isInExcludedPath = excludePaths.some(excluded => 
      path.includes('/' + excluded.toLowerCase() + '/') || 
      path.startsWith(excluded.toLowerCase() + '/')
    );
    if (isInExcludedPath) return false;

    // Exclude files with test-related patterns in filename
    const hasExcludedPattern = excludeFilenames.some(pattern => 
      filename.includes(pattern.toLowerCase())
    );
    if (hasExcludedPattern) return false;

    // Additional check: filename ends with test/spec pattern (before extension)
    // e.g., user.test.js, api.spec.ts, myfile_test.py
    const testSuffixPatterns = ['test', 'spec', 'tests', 'specs'];
    const endsWithTestPattern = testSuffixPatterns.some(suffix => {
      // Check if filename (without extension) ends with these patterns
      return filenameWithoutExt.endsWith('.' + suffix) || 
             filenameWithoutExt.endsWith('_' + suffix) || 
             filenameWithoutExt.endsWith('-' + suffix);
    });
    if (endsWithTestPattern) return false;

    // Additional check: filename starts with test/spec pattern
    // e.g., test_user.py, spec_api.js
    const startsWithTestPattern = testSuffixPatterns.some(prefix => {
      return filenameWithoutExt.startsWith(prefix + '.') || 
             filenameWithoutExt.startsWith(prefix + '_') || 
             filenameWithoutExt.startsWith(prefix + '-');
    });
    if (startsWithTestPattern) return false;

    // Exclude very large files
    if (item.size && item.size > 500000) return false;

    return true;
  })
  .slice(0, maxFiles)  // Limit based on config
  .map(file => {
    let language = 'unknown';
    for (const [lang, exts] of Object.entries(testableExtensions)) {
      if (exts.some(ext => file.path.endsWith(ext))) {
        language = lang;
        break;
      }
    }

    return {
      path: file.path,
      sha: file.sha,
      size: file.size,
      url: file.url,
      language: language,
      owner: owner,
      repo: repo,
      branch: branch
    };
  });

const filesByLanguage = sourceFiles.reduce((acc, file) => {
  if (!acc[file.language]) {
    acc[file.language] = [];
  }
  acc[file.language].push(file);
  return acc;
}, {});

const languageStats = Object.entries(filesByLanguage).map(([lang, files]) => ({
  language: lang,
  count: files.length,
  totalSize: files.reduce((sum, f) => sum + (f.size || 0), 0)
}));

return [{
  json: {
    owner,
    repo,
    branch,
    processingMode: config.processingMode,
    fetchFileContent: config.fetchFileContent,
    batchSize: config.batchSize,
    totalFiles: treeData.length,
    sourceFilesCount: sourceFiles.length,
    sourceFiles: sourceFiles,
    filesByLanguage: filesByLanguage,
    languageStats: languageStats,
    repositoryUrl: `https://github.com/${owner}/${repo}`
  }
}];

// Apply user-configured exclude patterns to the discovered sourceFiles
const repo = $('Process File Structure').first().json || {};
const config = $('Configuration').first().json || {};

// Parse patterns (supports JSON array string, comma-separated string, and regex via 're:pattern' or '/pattern/flags')
let rawPatterns = [];
try {
  const raw = config.excludePatterns;
  if (Array.isArray(raw)) rawPatterns = raw;
  else if (typeof raw === 'string') {
    const s = raw.trim();
    if (s.startsWith('[')) {
      try { rawPatterns = JSON.parse(s); } catch (e) { rawPatterns = s.split(','); }
    } else {
      rawPatterns = s.split(',');
    }
  }
} catch (e) { rawPatterns = []; }

const patterns = rawPatterns.map(p => {
  try {
    if (typeof p !== 'string') p = String(p);
    const pp = p.trim();
    if (!pp) return null;
    // regex with re: prefix
    if (pp.startsWith('re:')) {
      try { return { type: 'regex', re: new RegExp(pp.slice(3), 'i') }; } catch (e) { return { type: 'string', value: pp.slice(3).toLowerCase() }; }
    }
    // regex with /.../flags style
    if (pp.startsWith('/') && pp.lastIndexOf('/') > 0) {
      const last = pp.lastIndexOf('/');
      const body = pp.slice(1, last);
      const flags = pp.slice(last + 1) || 'i';
      try { return { type: 'regex', re: new RegExp(body, flags.includes('i') ? flags : flags + 'i') }; } catch (e) { return { type: 'string', value: pp.toLowerCase() }; }
    }
    return { type: 'string', value: pp.toLowerCase() };
  } catch (e) { return null; }
}).filter(Boolean);

function matchesPattern(filePath, filename, filenameNoExt) {
  if (!patterns || patterns.length === 0) return false;
  for (const p of patterns) {
    if (!p) continue;
    if (p.type === 'regex') {
      try {
        if (p.re.test(filePath) || p.re.test(filename) || p.re.test(filenameNoExt)) return true;
      } catch (e) { continue; }
    } else {
      if ((filePath || '').includes(p.value) || (filename || '').includes(p.value) || (filenameNoExt || '').includes(p.value)) return true;
    }
  }
  return false;
}

const originalFiles = repo.sourceFiles || [];
const filtered = originalFiles.filter(f => {
  const path = (f.path || '').toLowerCase();
  const filename = path.split('/').pop() || '';
  const filenameNoExt = filename.indexOf('.') > -1 ? filename.substring(0, filename.lastIndexOf('.')) : filename;
  return !matchesPattern(path, filename, filenameNoExt);
});

const filesByLanguage = filtered.reduce((acc, file) => { if (!acc[file.language]) acc[file.language] = []; acc[file.language].push(file); return acc; }, {});
const languageStats = Object.entries(filesByLanguage).map(([lang, files]) => ({ language: lang, count: files.length, totalSize: files.reduce((s, f) => s + (f.size || 0), 0) }));

return [{ json: { ...repo, sourceFilesCount: filtered.length, sourceFiles: filtered, filesByLanguage: filesByLanguage, languageStats: languageStats } }];

// Decode base64 content and prepare for AI
const item = $input.first().json;
const fileInfo = $input.first().json;

let fileContent = '';
if (item.content && item.encoding === 'base64') {
  fileContent = Buffer.from(item.content, 'base64').toString('utf-8');
}

return [{
  json: {
    filePath: item.path,
    fileName: item.name,
    language: fileInfo.language,
    size: item.size,
    content: fileContent,
    sha: item.sha,
    owner: fileInfo.owner,
    repo: fileInfo.repo
  }
}];

// Prepare batch processing
const data = $input.first().json;
const batchSize = data.batchSize || 10;
const sourceFiles = data.sourceFiles;

// Create batches
const batches = [];
for (let i = 0; i < sourceFiles.length; i += batchSize) {
  batches.push({
    batchNumber: Math.floor(i / batchSize) + 1,
    files: sourceFiles.slice(i, i + batchSize),
    owner: data.owner,
    repo: data.repo,
    branch: data.branch,
    totalBatches: Math.ceil(sourceFiles.length / batchSize)
  });
}

return batches.map(batch => ({ json: batch }));

// Parse and aggregate batch results
const items = $input.all();
const repoInfo = $('Apply Exclude Patterns').first().json;

let allTestCases = [];
let failedItems = [];

// Helper function to convert JS template literals and Python triple quotes to valid JSON
function normalizeToJSON(str) {
  let normalized = str;

  // Remove markdown code fences
  normalized = normalized.replace(/```json\s*/g, '').replace(/```\s*/g, '');

  // Find the array or object structure
  const arrayMatch = normalized.match(/\[\s*{[\s\S]*}\s*\]/);
  const targetStr = arrayMatch ? arrayMatch[0] : normalized;

  // Replace template literals (`...`) with proper JSON strings
  // This regex finds backtick strings and converts them to double-quoted strings
  let result = targetStr.replace(/`([^`]*)`/gs, (match, content) => {
    // Escape special JSON characters in the content
    const escaped = content
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"')
      .replace(/\n/g, '\\n')
      .replace(/\r/g, '\\r')
      .replace(/\t/g, '\\t');
    return `"${escaped}"`;
  });

  // Replace Python triple-quoted strings ("""...""") with proper JSON strings
  result = result.replace(/"""([^"]*)?"""/gs, (match, content) => {
    const escaped = content
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"')
      .replace(/\n/g, '\\n')
      .replace(/\r/g, '\\r')
      .replace(/\t/g, '\\t');
    return `"${escaped}"`;
  });

  // Remove trailing commas before closing braces/brackets
  result = result.replace(/,\s*([\]}])/g, '$1');

  // Ensure property names are double-quoted
  result = result.replace(/([{,]\s*)([a-zA-Z_][a-zA-Z0-9_]*)\s*:/g, '$1"$2":');

  return result;
}

items.forEach((item, itemIndex) => {
  let output = item.json.output;

  let testCases;
  try {
    // If already an object, use it directly
    if (typeof output === 'object' && output !== null) {
      testCases = output;
    } else if (typeof output === 'string') {
      // Strategy 1: Try direct JSON parse
      try {
        testCases = JSON.parse(output);
      } catch (parseError) {
        // Strategy 2: Normalize template literals and triple quotes, then parse
        try {
          const normalized = normalizeToJSON(output);
          testCases = JSON.parse(normalized);
        } catch (normalizeError) {
          // Strategy 3: Try to use eval as last resort for JS object literals
          try {
            // Extract just the array/object part
            let cleaned = output.replace(/```json\s*/g, '').replace(/```\s*/g, '');
            const arrayMatch = cleaned.match(/\[\s*{[\s\S]*}\s*\]/);
            if (arrayMatch) {
              // Wrap in parentheses and eval to parse JS object literal
              testCases = eval(`(${arrayMatch[0]})`);
            } else {
              const objectMatch = cleaned.match(/{[\s\S]*}/);
              if (objectMatch) {
                testCases = eval(`(${objectMatch[0]})`);
              } else {
                throw new Error('No valid JSON or JS object pattern found');
              }
            }
          } catch (evalError) {
            console.error(`Failed to parse item ${itemIndex}:`, evalError.message);
            failedItems.push({ 
              itemIndex, 
              error: evalError.message, 
              output: output.substring(0, 500),
              parseAttempts: ['JSON.parse', 'normalizeToJSON', 'eval']
            });
            testCases = [];
          }
        }
      }
    } else {
      testCases = [];
    }
  } catch (e) {
    console.error(`Error processing item ${itemIndex}:`, e.message);
    failedItems.push({ itemIndex, error: e.message });
    testCases = [];
  }

  // Ensure testCases is an array
  if (!Array.isArray(testCases)) {
    testCases = testCases ? [testCases] : [];
  }

  allTestCases = allTestCases.concat(testCases);
});

const formattedOutput = {
  repository: {
    owner: repoInfo.owner,
    repo: repoInfo.repo,
    branch: repoInfo.branch,
    url: repoInfo.repositoryUrl
  },
  processingMode: repoInfo.processingMode,
  summary: {
    totalSourceFiles: repoInfo.sourceFilesCount,
    filesAnalyzed: allTestCases.length,
    generatedAt: new Date().toISOString(),
    batchesProcessed: items.length,
    failedItems: failedItems.length,
    successfulItems: items.length - failedItems.length
  },
  testFiles: allTestCases.map((tc, index) => ({
    id: index + 1,
    sourceFile: tc.sourceFile,
    testFile: tc.testFile,
    framework: tc.framework || 'jest',
    language: tc.language,
    testFileContent: tc.testFileContent || '',
    dependencies: tc.dependencies || [],
    coverage: tc.coverage || {},
    runCommand: tc.runCommand || '',
    notes: tc.notes || ''
  })),
  errors: failedItems.length > 0 ? failedItems : undefined
};

return [{ json: formattedOutput }];

// Attach repository context and prepare branch check
const formatted = $input.first().json;
const repo = formatted.repository || {};
const config = $('Configuration').first().json;
const testFiles = formatted.testFiles || [];
const enriched = testFiles.map(tf => ({ ...tf, owner: tf.owner || repo.owner, repo: tf.repo || repo.repo, branch: tf.branch || repo.branch }));

return [{ json: { 
  testFiles: enriched,
  owner: repo.owner,
  repo: repo.repo,
  testBranch: config.testBranch,
  sourceBranch: config.branch,
  checkBranchUrl: `https://api.github.com/repos/${repo.owner}/${repo.repo}/branches/${config.testBranch}`
} }];

// Check if branch exists
const branchCheckResult = $input.first().json;
const repoContext = $('Attach Repo Context').first().json;

const hasError = branchCheckResult.error !== undefined;
const statusCode = branchCheckResult.statusCode || (hasError ? 404 : 200);
const branchExists = !hasError && statusCode === 200;

return [{
  json: {
    ...repoContext,
    branchExists: branchExists,
    statusCode: statusCode
  }
}];

// Pass through the test files for splitting
const repoContext = $('Process Branch Check').first().json;
return [{ json: repoContext }];

// Pass through the test files for splitting (branch already exists)
const repoContext = $input.first().json;
return [{ json: repoContext }];

// Debug: Check testFiles array before split
const item = $input.first().json;
console.log('=== BEFORE SPLIT DEBUG ===');
console.log('Has testFiles:', !!item.testFiles);
console.log('testFiles is array:', Array.isArray(item.testFiles));
console.log('testFiles length:', item.testFiles ? item.testFiles.length : 0);
if (item.testFiles && item.testFiles.length > 0) {
  console.log('First test file:', item.testFiles[0].sourceFile);
  console.log('Last test file:', item.testFiles[item.testFiles.length - 1].sourceFile);
}
return $input.all();

// Debug: Log what we received after split - process ALL items
const items = $input.all();
console.log('=== SPLIT ITEM DEBUG ===');
console.log('Total items after split:', items.length);

for (let i = 0; i < items.length; i++) {
  const item = items[i].json;
  console.log(`\n--- Item ${i + 1} ---`);
  console.log('Item keys:', Object.keys(item));
  console.log('Source file:', item.sourceFile);
  console.log('Test file:', item.testFile);
  console.log('Has testFileContent:', !!item.testFileContent);
  console.log('Content length:', item.testFileContent ? item.testFileContent.length : 0);
}

// Return ALL items
return items;

// Prepare commit payload for GitHub - process current item
const item = $input.item.json || {};
const content = item.testFileContent || item.data || '';
const config = $('Configuration').first().json;

// Helper function to extract component/module name from test content
function extractSourceNameFromContent(content, language) {
  if (!content) return null;

  // For JavaScript/TypeScript: look for import statements or describe blocks
  if (language === 'javascript' || language === 'typescript' || !language) {
    // Try to find: import ComponentName from '...'
    const importMatch = content.match(/import\s+(\w+)\s+from\s+['"][^'"]+['"]/m);
    if (importMatch && importMatch[1] && importMatch[1] !== 'React' && importMatch[1] !== 'pytest') {
      return importMatch[1];
    }

    // Try to find: describe('ComponentName', ...)
    const describeMatch = content.match(/describe\(['"]([\w]+)['"]/m);
    if (describeMatch && describeMatch[1]) {
      return describeMatch[1];
    }
  }

  // For Python: look for import statements or class/function names
  if (language === 'python') {
    // Try to find: from module.submodule import ClassName
    const fromImportMatch = content.match(/from\s+[\w.]+\s+import\s+(\w+)/m);
    if (fromImportMatch && fromImportMatch[1] && fromImportMatch[1] !== '*') {
      return fromImportMatch[1];
    }

    // Try to find: import module.ClassName
    const importMatch = content.match(/import\s+([\w.]+)/m);
    if (importMatch && importMatch[1]) {
      const parts = importMatch[1].split('.');
      return parts[parts.length - 1];
    }
  }

  return null;
}

// Determine the target path and filename
let targetPath = '';
let sourceFile = item.sourceFile || '';
const language = item.language || 'javascript';

// If sourceFile is empty, try to extract it from test content
if (!sourceFile || !sourceFile.trim()) {
  const extractedName = extractSourceNameFromContent(content, language);
  if (extractedName) {
    // Build a reasonable source file path based on extracted name
    if (language === 'python') {
      sourceFile = extractedName.toLowerCase() + '.py';
    } else if (language === 'typescript') {
      sourceFile = extractedName + '.tsx';
    } else {
      sourceFile = extractedName + '.js';
    }
  }
}

// Strategy 1: Use testFile if provided by AI (already includes full path)
if (item.testFile && item.testFile.trim() && !item.testFile.includes('generated_test_')) {
  targetPath = item.testFile;
  // Extract sourceFile from testFile if not provided
  if (!sourceFile) {
    const testFileName = targetPath.split('/').pop();
    sourceFile = testFileName.replace(/\.(test|spec)/, '').replace(/^test[_-]/, '');
  }
} 
// Strategy 2: Build from sourceFile
else if (sourceFile && sourceFile.trim()) {
  const pathParts = sourceFile.split('/');
  const fileName = pathParts[pathParts.length - 1];

  // Determine test file extension based on language or source extension
  const sourceExt = fileName.substring(fileName.lastIndexOf('.'));

  // Map languages to test file extensions
  const testExtMap = {
    'typescript': '.test.ts',
    'javascript': '.test.js',
    'python': '_test.py',
    'java': 'Test.java',
    'go': '_test.go',
    'ruby': '_spec.rb',
    'php': 'Test.php'
  };

  let testExt = testExtMap[language] || '.test' + sourceExt;

  // For TypeScript/JavaScript React files
  if (sourceExt === '.tsx') testExt = '.test.tsx';
  else if (sourceExt === '.jsx') testExt = '.test.jsx';
  else if (sourceExt === '.ts') testExt = '.test.ts';
  else if (sourceExt === '.js') testExt = '.test.js';
  else if (sourceExt === '.py') testExt = '_test.py';

  // Remove original extension and add test suffix
  const dotIndex = fileName.lastIndexOf('.');
  const nameWithoutExt = dotIndex > 0 ? fileName.substring(0, dotIndex) : fileName;

  // Build test filename
  let testFileName;
  if (language === 'python') {
    testFileName = `test_${nameWithoutExt}.py`;
  } else if (language === 'java') {
    testFileName = `${nameWithoutExt}Test.java`;
  } else {
    testFileName = `${nameWithoutExt}${testExt}`;
  }

  // Build full path: tests/auto-generated/{language}/{testFileName}
  targetPath = `tests/auto-generated/${language}/${testFileName}`;
} 
// Strategy 3: Generate unique filename as fallback
else {
  const timestamp = Date.now();
  const randomSuffix = Math.random().toString(36).substring(2, 15);
  const ext = language === 'python' ? '.py' : language === 'typescript' ? '.ts' : '.js';
  const testFileName = `generated_test_${timestamp}_${randomSuffix}${ext}`;
  targetPath = `tests/auto-generated/${language}/${testFileName}`;
  sourceFile = testFileName;
}

// Base64 encode the content
const contentBase64 = Buffer.from(content, 'utf8').toString('base64');

// Get owner/repo from item
const owner = item.owner || config.owner || 'unknown-owner';
const repo = item.repo || config.repo || 'unknown-repo';
const branch = config.testBranch;  // Use test branch directly

// Extract filename for commit message and local export
const pathParts = targetPath.split('/');
const localFileName = pathParts[pathParts.length - 1];
const commitMessage = `chore(tests): add auto-generated test for ${sourceFile || localFileName}`;

// Return data for both GitHub commit and local file creation
return { json: { owner, repo, branch, targetPath, contentBase64, commitMessage, testFile: targetPath, testFileContent: content, data: content, localFileName, sourceFile } };

// Preserve commit data and prepare for branch check
const commitData = $input.first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...commitData,
    testBranch: config.testBranch,
    checkBranchUrl: `https://api.github.com/repos/${commitData.owner}/${commitData.repo}/branches/${config.testBranch}`
  }
}];

// Merge the branch check result with commit data
const branchCheckResult = $input.first().json;
const commitData = $('Preserve Commit Data').first().json;

// Check if the request failed (error property exists) or succeeded
const hasError = branchCheckResult.error !== undefined;
const statusCode = branchCheckResult.statusCode || (hasError ? 404 : 200);
const branchExists = !hasError && statusCode === 200;

return [{
  json: {
    ...commitData,
    branchExists: branchExists,
    statusCode: statusCode
  }
}];

// Get the latest commit SHA from the main branch to create new branch from it
const data = $input.first().json;
const prepareCommitData = $('Prepare Git Commit').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    owner: prepareCommitData.owner,
    repo: prepareCommitData.repo,
    sourceBranch: config.branch,
    testBranch: config.testBranch,
    ...prepareCommitData
  }
}];

// Merge the branch creation result with the commit data
const prepareCommitData = $('Prepare Git Commit').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...prepareCommitData,
    branch: config.testBranch  // Override branch to use test branch
  }
}];

// For existing branch, just pass through with test branch override
const prepareCommitData = $('Preserve Commit Data').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...prepareCommitData,
    branch: config.testBranch  // Override branch to use test branch
  }
}];

// Collect all test files and prepare for bulk commit using GitHub Tree API
const items = $input.all();

console.log('=== AGGREGATE ALL TEST FILES DEBUG ===');
console.log('Total items received:', items.length);

if (items.length === 0) {
  throw new Error('No items to commit');
}

const firstItem = items[0].json;
console.log('First item keys:', Object.keys(firstItem));
console.log('First item owner:', firstItem.owner);
console.log('First item targetPath:', firstItem.targetPath);
console.log('First item has contentBase64:', !!firstItem.contentBase64);

// Group files by owner/repo/branch
const owner = firstItem.owner;
const repo = firstItem.repo;
const branch = firstItem.branch;

// Prepare tree entries for all files
const treeEntries = items.map((item, index) => {
  const data = item.json;
  console.log(`Item ${index}: path=${data.targetPath}, hasContent=${!!data.contentBase64}`);

  // Decode base64 content to UTF-8 for GitHub API
  const content = data.contentBase64 
    ? Buffer.from(data.contentBase64, 'base64').toString('utf-8')
    : data.testFileContent || data.data || '';

  return {
    path: data.targetPath,
    mode: '100644',  // Regular file
    type: 'blob',
    content: content
  };
});

const commitMessage = `chore(tests): add ${items.length} auto-generated test files\n\nGenerated tests for:\n${items.map(item => `- ${item.json.localFileName || item.json.sourceFile || 'unknown'}`).join('\n')}`;

console.log('Commit message:', commitMessage);
console.log('Tree entries count:', treeEntries.length);

return [{
  json: {
    owner,
    repo,
    branch,
    treeEntries,
    commitMessage,
    fileCount: items.length,
    files: items.map(item => item.json.targetPath)
  }
}];

// Extract base tree SHA from branch reference
const response = $input.first().json;
const commitData = $('Aggregate All Test Files').first().json;

return [{
  json: {
    ...commitData,
    baseCommitSha: response.object.sha
  }
}];

// Extract tree SHA and prepare for creating new tree
const commitInfo = $input.first().json;
const bulkData = $('Extract Base Commit SHA').first().json;

return [{
  json: {
    ...bulkData,
    baseTreeSha: commitInfo.tree.sha
  }
}];

// Prepare commit with new tree
const treeResponse = $input.first().json;
const bulkData = $('Extract Tree SHA').first().json;

return [{
  json: {
    ...bulkData,
    newTreeSha: treeResponse.sha
  }
}];

// Extract commit SHA for updating branch reference
const commitResponse = $input.first().json;
const bulkData = $('Prepare Commit Data').first().json;

return [{
  json: {
    ...bulkData,
    newCommitSha: commitResponse.sha
  }
}];
