// Extract file tree from GitHub API response
const treeData = $input.first().json.tree;
const config = $('Configuration').first().json;
const owner = config.owner;
const repo = config.repo;
const branch = config.branch;
const maxFiles = config.maxFilesToProcess || 100;

// Define testable file extensions by language/framework
const testableExtensions = {
  javascript: ['.js', '.jsx', '.mjs', '.cjs'],
  typescript: ['.ts', '.tsx'],
  python: ['.py'],
  java: ['.java', '.kt', '.kts'],
  csharp: ['.cs'],
  go: ['.go'],
  ruby: ['.rb'],
  php: ['.php'],
  rust: ['.rs'],
  swift: ['.swift'],
  cpp: ['.c', '.cpp', '.cc', '.cxx', '.h', '.hpp'],
  scala: ['.scala'],
  vue: ['.vue'],
  svelte: ['.svelte']
};

const allTestableExtensions = Object.values(testableExtensions).flat();

const excludePaths = [
  'test', 'tests', '__tests__', 'spec', 'specs',
  'node_modules', 'vendor', 'dist', 'build', 'out',
  '.git', '.github', '.vscode', '.idea',
  'coverage', 'docs', 'documentation',
  'examples', 'demo', 'samples'
];

const excludeFilenames = [
  'jest.config', 'webpack.config', 'babel.config',
  'tsconfig', 'eslint', 'prettier',
  '.test.', '.spec.', '_test.', '_spec.',
  'test.', 'spec.', 'mock', 'fixture'
];

const sourceFiles = treeData
  .filter(item => {
    if (item.type !== 'blob') return false;

    const path = item.path.toLowerCase();
    const filename = path.split('/').pop();

    const hasTestableExt = allTestableExtensions.some(ext => 
      item.path.endsWith(ext)
    );
    if (!hasTestableExt) return false;

    const isInExcludedPath = excludePaths.some(excluded => 
      path.includes('/' + excluded.toLowerCase() + '/') || 
      path.startsWith(excluded.toLowerCase() + '/')
    );
    if (isInExcludedPath) return false;

    const hasExcludedPattern = excludeFilenames.some(pattern => 
      filename.includes(pattern.toLowerCase())
    );
    if (hasExcludedPattern) return false;

    if (item.size && item.size > 500000) return false;

    return true;
  })
  .slice(0, maxFiles)  // Limit based on config
  .map(file => {
    let language = 'unknown';
    for (const [lang, exts] of Object.entries(testableExtensions)) {
      if (exts.some(ext => file.path.endsWith(ext))) {
        language = lang;
        break;
      }
    }

    return {
      path: file.path,
      sha: file.sha,
      size: file.size,
      url: file.url,
      language: language,
      owner: owner,
      repo: repo,
      branch: branch
    };
  });

const filesByLanguage = sourceFiles.reduce((acc, file) => {
  if (!acc[file.language]) {
    acc[file.language] = [];
  }
  acc[file.language].push(file);
  return acc;
}, {});

const languageStats = Object.entries(filesByLanguage).map(([lang, files]) => ({
  language: lang,
  count: files.length,
  totalSize: files.reduce((sum, f) => sum + (f.size || 0), 0)
}));

return [{
  json: {
    owner,
    repo,
    branch,
    processingMode: config.processingMode,
    fetchFileContent: config.fetchFileContent,
    batchSize: config.batchSize,
    totalFiles: treeData.length,
    sourceFilesCount: sourceFiles.length,
    sourceFiles: sourceFiles,
    filesByLanguage: filesByLanguage,
    languageStats: languageStats,
    repositoryUrl: `https://github.com/${owner}/${repo}`
  }
}];

// Decode base64 content and prepare for AI
const item = $input.first().json;
const fileInfo = $input.first().json;

let fileContent = '';
if (item.content && item.encoding === 'base64') {
  fileContent = Buffer.from(item.content, 'base64').toString('utf-8');
}

return [{
  json: {
    filePath: item.path,
    fileName: item.name,
    language: fileInfo.language,
    size: item.size,
    content: fileContent,
    sha: item.sha,
    owner: fileInfo.owner,
    repo: fileInfo.repo
  }
}];

// Prepare batch processing
const data = $input.first().json;
const batchSize = data.batchSize || 10;
const sourceFiles = data.sourceFiles;

// Create batches
const batches = [];
for (let i = 0; i < sourceFiles.length; i += batchSize) {
  batches.push({
    batchNumber: Math.floor(i / batchSize) + 1,
    files: sourceFiles.slice(i, i + batchSize),
    owner: data.owner,
    repo: data.repo,
    branch: data.branch,
    totalBatches: Math.ceil(sourceFiles.length / batchSize)
  });
}

return batches.map(batch => ({ json: batch }));

// Parse and aggregate batch results
const items = $input.all();
const repoInfo = $('Process File Structure').first().json;

let allTestCases = [];

items.forEach(item => {
  const output = item.json.output;

  let testCases;
  try {
    testCases = typeof output === 'string' ? JSON.parse(output) : output;
  } catch (e) {
    const jsonMatch = output.match(/\[\s*{[\s\S]*}\s*\]/);
    if (jsonMatch) {
      testCases = JSON.parse(jsonMatch[0]);
    } else {
      testCases = [];
    }
  }

  if (!Array.isArray(testCases)) {
    testCases = [testCases];
  }

  allTestCases = allTestCases.concat(testCases);
});

const formattedOutput = {
  repository: {
    owner: repoInfo.owner,
    repo: repoInfo.repo,
    branch: repoInfo.branch,
    url: repoInfo.repositoryUrl
  },
  processingMode: repoInfo.processingMode,
  summary: {
    totalSourceFiles: repoInfo.sourceFilesCount,
    filesAnalyzed: allTestCases.length,
    generatedAt: new Date().toISOString(),
    batchesProcessed: items.length
  },
  testFiles: allTestCases.map((tc, index) => ({
    id: index + 1,
    sourceFile: tc.sourceFile,
    testFile: tc.testFile,
    framework: tc.framework || 'jest',
    language: tc.language,
    testFileContent: tc.testFileContent || '',
    dependencies: tc.dependencies || [],
    coverage: tc.coverage || {},
    runCommand: tc.runCommand || '',
    notes: tc.notes || ''
  }))
};

return [{ json: formattedOutput }];

// Attach repository context to each testFile entry
const formatted = $input.first().json;
const repo = formatted.repository || {};
const testFiles = formatted.testFiles || [];
const enriched = testFiles.map(tf => ({ ...tf, owner: tf.owner || repo.owner, repo: tf.repo || repo.repo, branch: tf.branch || repo.branch }));
return [{ json: { testFiles: enriched } }];

// Prepare commit payload for GitHub
const item = $input.first().json || {};
const content = item.testFileContent || item.data || '';

// Get original source filename
const sourceFile = item.sourceFile || '';
let baseFileName = '';
if (sourceFile) {
  const pathParts = sourceFile.split('/');
  baseFileName = pathParts[pathParts.length - 1];
  // Remove extension and add test suffix
  const dotIndex = baseFileName.lastIndexOf('.');
  if (dotIndex > 0) {
    const nameWithoutExt = baseFileName.substring(0, dotIndex);
    const ext = baseFileName.substring(dotIndex);
    baseFileName = `${nameWithoutExt}.test${ext}`;
  } else {
    baseFileName = `${baseFileName}.test`;
  }
} else {
  baseFileName = `generated_test_${Date.now()}.js`;
}

// Build target path under tests/auto-generated
const targetPath = item.testFile || `tests/auto-generated/${baseFileName}`;

// Base64 encode the content
const contentBase64 = Buffer.from(content, 'utf8').toString('base64');

// Get owner/repo/branch from item or Configuration node
let owner = item.owner || (item.repository && item.repository.owner) || undefined;
let repo = item.repo || (item.repository && item.repository.repo) || undefined;
let branch = item.branch || (item.repository && item.repository.branch) || undefined;
try {
  const cfg = $('Configuration').first().json || {};
  owner = owner || cfg.owner || 'unknown-owner';
  repo = repo || cfg.repo || 'unknown-repo';
  branch = branch || cfg.branch || 'main';
} catch (e) {
  owner = owner || 'unknown-owner';
  repo = repo || 'unknown-repo';
  branch = branch || 'main';
}

// Extract filename for commit message and local export
const pathParts = targetPath.split('/');
const localFileName = pathParts[pathParts.length - 1];
const commitMessage = `chore(tests): add auto-generated test for ${sourceFile || localFileName}`;

// Return data for both GitHub commit and local file creation
return [{ json: { owner, repo, branch, targetPath, contentBase64, commitMessage, testFile: item.testFile, testFileContent: content, data: content, localFileName } }];

// Preserve commit data and prepare for branch check
const commitData = $input.first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...commitData,
    testBranch: config.testBranch,
    checkBranchUrl: `https://api.github.com/repos/${commitData.owner}/${commitData.repo}/branches/${config.testBranch}`
  }
}];

// Merge the branch check result with commit data
const branchCheckResult = $input.first().json;
const commitData = $('Preserve Commit Data').first().json;

// Check if the request failed (error property exists) or succeeded
const hasError = branchCheckResult.error !== undefined;
const statusCode = branchCheckResult.statusCode || (hasError ? 404 : 200);
const branchExists = !hasError && statusCode === 200;

return [{
  json: {
    ...commitData,
    branchExists: branchExists,
    statusCode: statusCode
  }
}];

// Get the latest commit SHA from the main branch to create new branch from it
const data = $input.first().json;
const prepareCommitData = $('Prepare Git Commit').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    owner: prepareCommitData.owner,
    repo: prepareCommitData.repo,
    sourceBranch: config.branch,
    testBranch: config.testBranch,
    ...prepareCommitData
  }
}];

// Merge the branch creation result with the commit data
const prepareCommitData = $('Prepare Git Commit').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...prepareCommitData,
    branch: config.testBranch  // Override branch to use test branch
  }
}];

// For existing branch, just pass through with test branch override
const prepareCommitData = $('Preserve Commit Data').first().json;
const config = $('Configuration').first().json;

return [{
  json: {
    ...prepareCommitData,
    branch: config.testBranch  // Override branch to use test branch
  }
}];
